mapper.select:
select count(*) as
listCnt
from board where memidx = ${memidx};

select *
from board
where memidx = ${memidx}
order by postidx
desc, postdate desc
LIMIT #{startList}, #{listSize}

mapper.update:
update member set
MEMNICK = #{MEMNICK},
MEMPHONE =
#{MEMPHONE}
where memidx = #{MEMIDX};

mapper.select:
select infocontent from mypage where memidx = #{arg0} and
infodate = #{arg1} ORDER BY FIELD(infotype, 'photo','food',
'kg','memo') DESC, infotype ASC;
